    /**
     * 题目中有哥非常重要的条件，即只有2个岛屿，且其中一个岛屿只有一个1，没看到这个条件导致我花了很长时间想思路 800+ms
     *
     *
     * @param A
     * @return
     */
    public int shortestBridge(int[][] A) {
        int x_length = A.length;
        int y_length = A[0].length;
        int ans = Integer.MAX_VALUE;


        boolean first = true;


        for (int i = 0; i < x_length; i++) {
            for (int j = 0; j < y_length; j++) {
                //找到第一片岛屿
                if (A[i][j] == 1 && first) {
                    ArrayDeque<int[]> queue = new ArrayDeque<>();
                    queue.add(new int[]{i, j});
                    while (!queue.isEmpty()) {
                        int[] pop = queue.pop();
                        if (A[pop[0]][pop[1]] == 0 || A[pop[0]][pop[1]] == 2) {
                            continue;
                        }
                        A[pop[0]][pop[1]] = 2;
                        if (pop[0] - 1 >= 0) {
                            queue.add(new int[]{pop[0] - 1, pop[1]});
                        }
                        if (pop[0] + 1 < x_length) {
                            queue.add(new int[]{pop[0] + 1, pop[1]});
                        }
                        if (pop[1] - 1 >= 0) {
                            queue.add(new int[]{pop[0], pop[1] - 1});
                        }
                        if (pop[1] + 1 < y_length) {
                            queue.add(new int[]{pop[0], pop[1] + 1});
                        }
                    }

                    first = false;
                } else if (A[i][j] == 1) {
                    //从第二个岛屿开始向外辐射，直到第一次遇到2
                    ArrayDeque<Pair> queue = new ArrayDeque<>();
                    queue.add(new Pair(i, j));

                    //用于去重
                    Set<Pair> set = new HashSet<>();

                    int value = 0;
                    xx:
                    while (!queue.isEmpty()) {
                        int size = queue.size();
                        for (int m = 0; m < size; m++) {
                            Pair pop = queue.pop();
                            set.add(pop);
                            if (A[pop.x][pop.y] == 2) {
                                break xx;
                            }
                            if (pop.x - 1 >= 0) {
                                Pair ints = new Pair(pop.x - 1, pop.y);
                                if (!set.contains(ints)) {
                                    queue.add(ints);
                                    set.add(ints);
                                }
                            }
                            if (pop.x + 1 < x_length) {
                                Pair ints = new Pair(pop.x + 1, pop.y);
                                if (!set.contains(ints)) {
                                    queue.add(ints);
                                    set.add(ints);
                                }
                            }
                            if (pop.y - 1 >= 0) {
                                Pair ints = new Pair(pop.x, pop.y - 1);
                                if (!set.contains(ints)) {
                                    queue.add(ints);
                                    set.add(ints);
                                }
                            }
                            if (pop.y + 1 < y_length) {
                                Pair ints = new Pair(pop.x, pop.y + 1);
                                if (!set.contains(ints)) {
                                    queue.add(ints);
                                    set.add(ints);
                                }
                            }
                        }
                        value++;
                        if (value >= ans) {
                            break;
                        }

                    }

                    ans = value;

                }
            }
        }


        return ans - 1;
    }


    /**
     * 和上面相同的思路，但是不是从1开始往四周，而是从2向四周 48ms，这里的Pair可以不用了，但是是copy上面的就不删了
     *
     * @param A
     * @return
     */
    public int shortestBridge2(int[][] A) {
        int x_length = A.length;
        int y_length = A[0].length;
        int ans = 0;

        boolean[][] visited = new boolean[x_length][y_length];

        ArrayDeque<Pair> pp = new ArrayDeque<>();
        mm:
        for (int i = 0; i < x_length; i++) {
            for (int j = 0; j < y_length; j++) {
                //找到第一片岛屿
                if (A[i][j] == 1) {
                    ArrayDeque<int[]> queue = new ArrayDeque<>();
                    queue.add(new int[]{i, j});
                    while (!queue.isEmpty()) {
                        int[] pop = queue.pop();
                        pp.add(new Pair(pop[0], pop[1]));


                        if (A[pop[0]][pop[1]] == 0 || A[pop[0]][pop[1]] == 2) {
                            continue;
                        }
                        A[pop[0]][pop[1]] = 2;
                        visited[pop[0]][pop[1]] = true;
                        if (pop[0] - 1 >= 0) {
                            queue.add(new int[]{pop[0] - 1, pop[1]});
                        }
                        if (pop[0] + 1 < x_length) {
                            queue.add(new int[]{pop[0] + 1, pop[1]});
                        }
                        if (pop[1] - 1 >= 0) {
                            queue.add(new int[]{pop[0], pop[1] - 1});
                        }
                        if (pop[1] + 1 < y_length) {
                            queue.add(new int[]{pop[0], pop[1] + 1});
                        }
                    }
                    break mm;
                }
            }
        }

        while (!pp.isEmpty()) {
            int size = pp.size();
            for (int i = 0; i < size; i++) {
                Pair pop = pp.pop();
                if (A[pop.x][pop.y] == 1) {
                    return ans ;
                }
                if (visited[pop.x][pop.y]) {
                    continue;
                }

                visited[pop.x][pop.y] = true;
                if (pop.x - 1 >= 0) {
                    Pair ints = new Pair(pop.x - 1, pop.y);
                    pp.add(ints);
                }
                if (pop.x + 1 < x_length) {
                    Pair ints = new Pair(pop.x + 1, pop.y);
                    pp.add(ints);
                }
                if (pop.y - 1 >= 0) {
                    Pair ints = new Pair(pop.x, pop.y - 1);
                    pp.add(ints);
                }
                if (pop.y + 1 < y_length) {
                    Pair ints = new Pair(pop.x, pop.y + 1);
                    pp.add(ints);
                }
            }
            ans++;
        }




        return  -1;
    }

    class Pair {
        int x;
        int y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;

            Pair pair = (Pair) o;

            if (x != pair.x) return false;
            return y == pair.y;
        }

        @Override
        public int hashCode() {
            int result = x;
            result = 31 * result + y;
            return result;
        }
    }
